import React, { useState } from 'react';

export default function RegistrationPage() {

  const [formData, setFormData] = useState({
    participantName: '',
    orgName: '',
    designation: '',
    emailID: '',
    phoneNumber: '',
    eventName: '',
    eventID: '',
    eventDate: '',
    eventOrganizer: ''
  });

  function handleSubmit(event){
    //Page will be reloaded and the input data will be lost
    event.preventDefault();
  }

  function handleChange(event){
    setFormData({
      ...formData,
      [event.target.id]: event.target.value
    });
  }

  return (
    <div>
      <form onSubmit = {handleSubmit}>
        <label> Name of the Participant: </label>
        <input type = 'text' id = 'participantName' value = {formData.participantName} onChange = {handleChange}/> <br/><br/>
        
        {/* <label for = 'orgName'> Name of the Organization: </label> */}
        {/* <input type = 'textbox' id = 'orgName' placeholder = 'Organization' value = {values.orgName}/> <br/><br/> */}
        {/*  */}
        {/* <label for = 'designation'> Designation of the Participant: </label> */}
        {/* <input type = 'textbox' id = 'designation' placeholder = 'Designation' value = {values.designation}/> <br/><br/> */}
        {/*  */}
        {/* <label for = 'emailID'> Email Address of the Participant: </label> */}
        {/* <input type = 'email' id = 'emailID' placeholder = 'youremail@abc.com' value = {values.emailID}/> <br/><br/> */}
        {/*  */}
        {/* <label for = 'phoneNumber'> Phone Number of the Participant: </label> */}
        {/* <input type = 'textbox' id = 'phoneNumber' placeholder = 'Phone Number' value = {values.phoneNumber}/> <br/><br/> */}
        {/*  */}
        {/* <label for = 'eventName'> Name of the Event: </label> */}
        {/* <input type = 'textbox' id = 'eventName' placeholder = 'Event Name' value = {values.eventName}/> <br/><br/> */}
        {/*  */}
        {/* <label for = 'eventID'> ID of the Event: </label> */}
        {/* <input type = 'textbox' id = 'eventID' placeholder = 'Event ID' value = {values.eventID}/> <br/><br/> */}
        {/*  */}
        {/* <label for = 'eventDate'> Date of the Event: </label> */}
        {/* <input type = 'date' id = 'eventDate' placeholder = 'Event Date' value = {values.eventDate}/> <br/><br/> */}
        {/*  */}
        {/* <label for = 'eventOrganizer'> Name of the Event Organiser: </label> */}
        {/* <input type = 'textbox' id = 'eventOrganizer' placeholder = 'Event Organiser' value = {values.eventOrganizer}/> <br/><br/> */}
{/*  */}
       <button type = 'submit' id = 'submitButton'> Submit </button>
      
      </form>
    </div>
  )
}
